import React from 'react';

function repartitionMoney(listFriends,listPayment,generateBill){
  const totalCost = listPayment.reduce((sum,payment)=>sum+=Number(payment.cost),0);
  const part = Math.round(totalCost/listFriends.length);
  console.log("la part vaut"+part);
  generateBill();
}

function Transactions({listFriends,listPayment,generateBill}) {
    return (
      <div className='mt-5 container'>
        <button className='col-4 btn btn-primary' onClick={() => repartitionMoney(listFriends,listPayment,generateBill)}>Generate Invoice</button>
      </div>
    )
  }

export default Transactions;
