import React from 'react';

function repartitionMoney(listFriends, listPayment, generateBill) {
  const totalCost = listPayment.reduce((sum, payment) => sum + Number(payment.cost), 0);
  const part = totalCost / listFriends.length;
  let listBill = generateBill();
  let listTransactions = [];
  let debtHolder = new Map();
  let bankFriend = new Map();

  for (let [name, cost] of listBill.entries()) {
    if (Number(cost) < part) {
      debtHolder.set(name, part - Number(cost));
    } else {
      bankFriend.set(name, Number(cost) - part);
    }
  }

  // Convert to arrays and sort by value descending
  let sortedDebtHolders = Array.from(debtHolder.entries()).sort((a, b) => b[1] - a[1]);
  let sortedBankFriends = Array.from(bankFriend.entries()).sort((a, b) => b[1] - a[1]);

  // Iterate over sorted debt holders
  for (let i = 0; i < sortedDebtHolders.length; i++) {
    let [debtor, debtAmount] = sortedDebtHolders[i];
    if (debtAmount === 0) continue;

    // Iterate over sorted bank friends
    for (let j = 0; j < sortedBankFriends.length; j++) {
      let [creditor, creditAmount] = sortedBankFriends[j];
      if (creditAmount === 0) continue;

      let transaction = { debtHolder: debtor, bank: creditor };

      if (debtAmount > creditAmount) {
        transaction.cost = creditAmount;
        debtAmount -= creditAmount;
        sortedBankFriends[j][1] = 0; // Reset the credit amount
      } else {
        transaction.cost = debtAmount;
        sortedBankFriends[j][1] -= debtAmount;
        debtAmount = 0;
      }

      listTransactions.push(transaction);

      // Update the debt amount for the current debtor
      sortedDebtHolders[i][1] = debtAmount;

      // Break the inner loop if the current debtor's debt is settled
      if (debtAmount === 0) {
        break;
      }
    }
  }

  console.log("Debt Holders Sorted:", new Map(sortedDebtHolders));
  console.log("Bank Friends Sorted:", new Map(sortedBankFriends));
  console.log("List of Transactions:", listTransactions);
}

function Transactions({listFriends,listPayment,generateBill}){
    return (
      <div className='mt-5 container'>
        <button className='col-4 btn btn-primary' onClick={() => repartitionMoney(listFriends,listPayment,generateBill)}>Generate Invoice</button>
      </div>
    )
  }

export default Transactions;
