import React, { useState,useRef } from 'react';


import Nav from './Nav';

import Friends from './pages/Friends';
import Expenses from './pages/Expenses';
import Transactions from './pages/Transactions';



function App() {
  const [page,setPage] = useState('Friends');
  const [listFriends,setListFriends] = useState([]);

  const inputRef = useRef(null);

  const handleDelete = (index) => {
    setListFriends(listFriends.filter((friend,i) => i !== index));

  }

  const addFriend = ()=>{
    const newFriendName = inputRef.current.value.trim();
    if (newFriendName) {
      setListFriends([...listFriends, newFriendName]);
      inputRef.current.value = '';
    }
  }

  const handleRoute = (e)=>{
    setPage(e.target.value);
  }

  const render = () => {
    switch (page) {
      case 'Friends':
        return <Friends ref={inputRef} addFriend={addFriend} listFriends={listFriends} handleDelete={handleDelete}/>;
      case 'Expenses':
        return <Expenses listFriends={listFriends}/>;
      case 'Transactions':
        return <Transactions />;
      default:
        return <Friends ref={inputRef} addFriend={addFriend} listFriends={listFriends} handleDelete={handleDelete}/>;
    }

  }

  return (
    <>
    <Nav handleRoute={handleRoute} />
    {render()}
    </>
);
}

export default App;
