import React from 'react';

function repartitionMoney(listFriends,listPayment,generateBill){
  const totalCost = listPayment.reduce((sum,payment) => sum += Number(payment.cost),0);
  const part = totalCost/listFriends.length;
  let listBill = generateBill();
  let listTransactions = [];
  let debtHolder = new Map();
  let bankFriend = new Map();

  for (let [name,cost] of listBill.entries()) {
    if (Number(cost) < part) {
      debtHolder.set(name,part-Number(cost));
    } else {
      bankFriend.set(name,Number(cost)-part);
    }
  }
  let sortedByValue = Array.from(debtHolder.entries()).sort((a, b) => {
    return b[1] - a[1];
  });
  let debtHolderSorted = new Map(sortedByValue);
  let bankSortedByValue = Array.from(bankFriend.entries()).sort((a, b) => {
    return b[1] - a[1];
  });
  console.log(debtHolderSorted);
  console.log(bankSortedByValue);
  for(let [name,cost] of debtHolderSorted.entries()){
    let [debtorName,debtAmount]=[name,cost];
    if (debtAmount === 0) continue;
    for(let j = 0; j < bankSortedByValue.length; j++){
      let [creditor, creditAmount] = bankSortedByValue[j];
      let transaction = {};
      transaction.debtHolder = debtorName;
      transaction.bank = creditor;
      if (cost > creditAmount) {
        transaction.cost = creditAmount;
        debtAmount -= creditAmount;
        listTransactions.push(transaction);
      } else {
        transaction.cost = debtAmount;
        debtAmount = 0;
        bankSortedByValue[j][1] -= debtAmount;
        listTransactions.push(transaction);
        break;
      }

    }
  }
  console.log(listTransactions);
}

function Transactions({listFriends,listPayment,generateBill}){
    return (
      <div className='mt-5 container'>
        <button className='col-4 btn btn-primary' onClick={() => repartitionMoney(listFriends,listPayment,generateBill)}>Generate Invoice</button>
      </div>
    )
  }

export default Transactions;
