import React, { useState } from 'react';

function App() {
  const [listTask,setListTask] = useState([]);
  const [inputText,setInputText] = useState("");
  const [id,setId]= useState(0);

  function addTask(task){
    setListTask([...listTask,task])    
  }

  const handleSubmit = (e)=>{
    e.preventDefault(); 
    let task= {name: inputText, completed: false, id:{id}};
    setId(id+1);
    console.log(task.name);
    addTask(task);
    setInputText("");
  }
  const handleChange = (e)=>{
    setInputText(e.target.value);
  }

  return (
    <div className="App">
      <Input handleSubmit={handleSubmit} handleChange={handleChange}/>
      <ListTask list={listTask}/>
    </div>
  );
}

function Input({handleSubmit,handleChange}){

  return (
    <form onSubmit = {handleSubmit} >
      <input 
      placeholder="Ajouter une tache"
      onChange={handleChange}
      />
      <button type="submit">Ajouter</button>
    </form>
  )
}

function ListTask({list}) {
  return (
    <ul>
      {list.map((task, index)=>{
        return(
        <li key={index}>
          <Task task = {task} />
        </li>
      )})}

    </ul>
  )  
}

function Task({task}){
  const supprTask = (index)=>{
    const newListTask = listTask.filter((id)=>{
      return index===id ? false : true;
    })
    setListTask(newListTask);
  }
  return(
    <div>
      <p>{task.name}</p>
      <button onClick={()=>supprTask(task.id)}>Supprimer</button>
    </div>
  )
}

export default App;
