import React, { useState } from 'react';

function App() {
  const [listTask, setListTask] = useState([]);
  const [inputText, setInputText] = useState("");
  const [id, setId] = useState(0);

  function addTask(task){
    setListTask([...listTask, task]);
  }

  const handleSubmit = (e) => {
    e.preventDefault(); 
    let task = { name: inputText, completed: false, id: id };
    setId(id + 1);
    console.log(id);
    addTask(task);
    const input = document.getElementById("input");
    input.value="";
  }

  const handleChange = (e) => {
    setInputText(e.target.value);
  }
  const toggle = (taskId)=>{
    const newListTask = listTask.map((task) => {
      if (taskId === task.id) {
        return {
          ...task,
          completed: !task.completed
        };
      } else {
        return task;
      }
    });
    setListTask(newListTask);
  }
  const supprTask = (taskId) => {
    const newListTask = listTask.filter(task => taskId !== task.id);
    setListTask(newListTask);
  };

  return (
    <div className="App">
      <Input handleSubmit={handleSubmit} handleChange={handleChange} />
      <ListTask list={listTask} supprTask={supprTask} toggle={toggle}/>
    </div>
  );
}

function Input({ handleSubmit, handleChange }) {
  return (
    <form onSubmit={handleSubmit}>
      <input 
        placeholder="Ajouter une tÃ¢che"
        onChange={handleChange}
        id="input"
      />
      <button type="submit">Ajouter</button>
    </form>
  );
}

function ListTask({ list, supprTask , toggle }) {
  return (
    <ul>
      {list.map((task, index) => (
        <li key={index}>
          <Task task={task} supprTask={supprTask} toggle={toggle}/>
        </li>
      ))}
    </ul>
  );
}

function Task({ task, supprTask ,toggle }) {
  const handleDelete = () => {
    supprTask(task.id);
  };
  const handleToggle = () => {
    toggle(task.id)
  }

  return (
    <div>
      <p style={{textDecoration: task.completed && 'line-through'}}>{task.name}</p>
      <button onClick={handleToggle}>{task.completed ? "UnDone" : "Done" }</button>
      <button onClick={handleDelete}>Supprimer</button>
    </div>
  );
}

export default App;
