import React, { useState } from 'react';

function App() {
  const [listTask,setListTask] = useState([]);
  const [inputText,setInputText] = useState("");

  function addTask(task){
    setListTask([...listTask,task])    
  }
  const supprTask = (id)=>{
    const newListTask = listTask.filter((task)=>{
      return id===task.id ? false:true;
    })
    setListTask
  }


  const handleSubmit = (e)=>{
    e.preventDefault(); 
    let task= {name: inputText, completed: false};
    console.log(task.name);
    addTask(task);
    setInputText("");
  }
  const handleChange = (e)=>{
    setInputText(e.target.value);
  }

  return (
    <div className="App">
      <Input handleSubmit={handleSubmit} handleChange={handleChange}/>
      <ListTask list={listTask} supprTask={()=> supprTask(id)}/>
    </div>
  );
}

function Input({handleSubmit,handleChange}){

  return (
    <form onSubmit = {handleSubmit} >
      <input 
      placeholder="Ajouter une tache"
      onChange={handleChange}
      />
      <button type="submit">Ajouter</button>
    </form>
  )
}

function ListTask({list,supprTask}) {
  return (
    <ul>
      {list.map((task, index)=>{
        return(
        <li key={index}>
          <Task supprTask={supprTask} task = {task} />
        </li>
      )})}

    </ul>
  )  
}

function Task({task,supprTask}){
  return(
    <div>
      <p>{task.name}</p>
      <button onClick={supprTask}>Supprimer</button>
    </div>
  )
}

export default App;
