import React, { useState } from 'react';

function App() {
  const [tasks, setTasks] = useState([]);

  const addTask = (task) => {
    setTasks([...tasks, task]);
  };

  return (
    <div className="App">
      <Input onAdd={addTask} />
      <div>
        {tasks.map((task, index) => (
          <Task key={index} task={task} />
        ))}
      </div>
    </div>
  );
}

const Input = ({ onAdd }) => {
  const [textInput, setTextInput] = useState("");

  const handleInputChange = (event) => {
    setTextInput(event.target.value);
  };
  function formatDate(date) {
    const day = String(date.getDate()).padStart(2, '0');
    const month = String(date.getMonth() + 1).padStart(2, '0'); // Les mois sont indexés de 0 à 11
    const year = String(date.getFullYear()).slice(-2); // Obtenir les deux derniers chiffres de l'année

    return `${day}-${month}-${year}`;
  }
  const handleSubmit = (event) => {
    event.preventDefault();
    if (textInput.trim() !== "") {
      const now = new Date();
      const newTask = { id: formatDate(now), text: textInput }; // Create a task object
      onAdd(newTask);
      setTextInput("");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        id="textInput"
        name="textInput"
        placeholder="Entrez votre texte ici"
        value={textInput}
        onChange={handleInputChange}
      />
      <button type="submit">Ajouter</button>
    </form>
  );
};

const Task = ({ task }) => {
  return (
    <div>
      <h3>Task ID: {task.id}</h3>
      <p>{task.text}</p>
    </div>
  );
};

export default App;
